import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [orderToken, setOrderToken] = useState('');
    const [paymentStatus, setPaymentStatus] = useState(null);

    // Function to create an order and get the token from the backend
    const createOrder = async () => {
        try {
            const { data } = await axios.post('http://localhost:5000/api/create-order', {
                amount: 100,  // The amount for the order
                currency: 'GBP',  // The currency (adjust as needed)
            });

            console.log('Order Response:', data); // Log the token to check if it's correct
            setOrderToken(data.token);  // Set the order token from the backend
            setPaymentStatus(null);  // Reset payment status
        } catch (error) {
            console.error('Error creating order:', error);
            setPaymentStatus('Order creation failed');
        }
    };

    // Handle the payment process when the user clicks the 'Pay' button
    const handlePayment = async () => {
        if (!orderToken) {
            alert('Order token not found. Create an order first.');
            return;
        }

        try {
            // Initialize the RevolutCheckout widget with the token
            const { payWithPopup } = await RevolutCheckout(orderToken);

            // Attach event listener to the Pay button
            const payButton = document.getElementById("pay-button");
            payButton.addEventListener("click", function () {
                payWithPopup({
                    email: "example.customer@example.com",  // Provide email if it's not included during order creation
                    onSuccess() {
                        // Handle successful payment
                        window.alert("Thank you for your payment!");
                    },
                    onError(error) {
                        // Handle payment error
                        window.alert(`Something went wrong. ${error}`);
                    }
                });
            });

        } catch (error) {
            console.error('Error initializing RevolutCheckout widget:', error);
            setPaymentStatus('Failed to initialize widget');
        }
    };

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h1>Revolut Card Payment Integration</h1>
            <button onClick={createOrder} style={styles.button}>Create Order</button>
            <br /><br />
            {orderToken && (
                <div>
                    <button id="pay-button" type="button" onClick={handlePayment} style={styles.payButton}>
                        Pay with Revolut
                    </button>
                </div>
            )}
            {paymentStatus && <p>Status: {paymentStatus}</p>}
        </div>
    );
};

// Simple CSS styling for the buttons
const styles = {
    button: {
        padding: '12px 24px',
        fontSize: '16px',
        cursor: 'pointer',
        backgroundColor: '#4CAF50',  // Green color for the button
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        margin: '10px',
    },
    payButton: {
        padding: '12px 24px',
        fontSize: '16px',
        cursor: 'pointer',
        backgroundColor: '#1e88e5',  // Blue color for the Pay button
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        margin: '10px',
    }
};

export default App;
