import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [orderToken, setOrderToken] = useState('');
    const [paymentStatus, setPaymentStatus] = useState(null);

    // Create order and get token from backend
    const createOrder = async () => {
        try {
            const { data } = await axios.post('http://localhost:5000/api/create-order', {
                amount: 100, // Amount in pence (100 = £1)
                currency: 'GBP',
            });

            if (data.token) {
                setOrderToken(data.token); // Set the order token to state
                setPaymentStatus(null); // Reset payment status
            } else {
                setPaymentStatus('Failed to get order token');
            }
        } catch (error) {
            console.error('Error creating order:', error);
            setPaymentStatus('Order creation failed');
        }
    };

    // Handle payment using the RevolutCheckout widget
    const handlePayment = async () => {
        if (!orderToken) {
            alert('Order token not found. Create an order first.');
            return;
        }

        try {
            const { payWithPopup } = await RevolutCheckout(orderToken); // Initialize Revolut Checkout with the order token

            // Handle payment popup
            payWithPopup({
                email: 'example.customer@example.com', // Include email if not provided during order creation
                onSuccess() {
                    setPaymentStatus('Payment successful');
                    alert('Thank you for your payment!');
                },
                onError(error) {
                    setPaymentStatus('Payment failed');
                    alert(`Something went wrong: ${error}`);
                }
            });
        } catch (error) {
            console.error('Error initializing widget:', error);
            setPaymentStatus('Failed to initialize widget');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Revolut Card Payment Integration</h1>
            <button onClick={createOrder}>Create Order</button>
            <br /><br />
            {orderToken && (
                <div>
                    <button onClick={handlePayment}>Pay with Revolut</button>
                </div>
            )}
            {paymentStatus && <p>Status: {paymentStatus}</p>}
        </div>
    );
};

export default App;
