import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [orderToken, setOrderToken] = useState('');
  const [paymentStatus, setPaymentStatus] = useState('');

  // Create an order and get the token
  const createOrder = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/create-order', {
        amount: 500,    // Example amount in cents (500 = 5 GBP)
        currency: 'GBP' // Example currency
      });

      console.log('Received order token:', response.data.token);
      setOrderToken(response.data.token); 

    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  // Handle payment with the Revolut Checkout widget
  const handlePayment = async () => {
    if (!orderToken) {
      console.error('No order token available');
      return;
    }

    try {
      const { payWithPopup } = await RevolutCheckout(orderToken);

      // Open the Revolut payment pop-up
      payWithPopup({
        email: 'customer@example.com',  // Replace with actual customer email
        onSuccess: () => {
          setPaymentStatus('Payment successful!');
          alert('Payment Successful');
        },
        onError: (error) => {
          setPaymentStatus('Payment failed');
          alert(`Payment Failed: ${error}`);
        },
      });
    } catch (error) {
      console.error('Error initializing payment:', error);
    }
  };

  return (
    <div>
      <button onClick={createOrder}>Create Order</button>
      {orderToken && (
        <div>
          <button onClick={handlePayment}>Pay with Revolut</button>
        </div>
      )}
      {paymentStatus && <div>{paymentStatus}</div>}
    </div>
  );
};

export default App;
