import React, { useState } from 'react';
import axios from 'axios';
import RevolutCheckout from '@revolut/checkout';

const App = () => {
    const [orderToken, setOrderToken] = useState('');
    const [paymentStatus, setPaymentStatus] = useState(null);

    // Create order and get token
    const createOrder = async () => {
        try {
            const { data } = await axios.post('http://localhost:5000/api/create-order', {
                amount: 100, // Specify the amount in pence, i.e., 100 = £1
                currency: 'GBP',
            });

            console.log('Received order token:', data.token); // Log the token for debugging
            setOrderToken(data.token);
            setPaymentStatus(null); // Reset payment status
        } catch (error) {
            console.error('Error creating order:', error);
            setPaymentStatus('Order creation failed');
        }
    };

    // Initializing the Revolut Checkout pop-up widget here for payment.
    const handlePayment = async () => {
        if (!orderToken) {
            alert('Order token not found. Create an order first.');
            return;
        }

        try {
            // Initialize the Revolut Checkout widget with the received token
            const { payWithPopup } = await RevolutCheckout(orderToken);

            // Define the payment button behavior
            const payButton = document.getElementById('pay-button');
            payButton.addEventListener('click', function () {
                const popUp = payWithPopup({
                    email: 'example.customer@example.com', // Optional: Add customer's email if needed
                    onSuccess() {
                        setPaymentStatus('Payment successful');
                        window.alert('Thank you!');
                    },
                    onError(error) {
                        setPaymentStatus('Payment failed');
                        window.alert(`Something went wrong. ${error}`);
                    },
                });
            });

        } catch (error) {
            console.error('Error initializing widget:', error);
            setPaymentStatus('Failed to initialize widget');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Revolut Card Payment Integration</h1>
            <button onClick={createOrder}>Create Order</button>
            <br /><br />
            {orderToken && (
                <div>
                    <button id="pay-button" onClick={handlePayment}>
                        Pay with Revolut
                    </button>
                    <div id="revolut-widget" style={{ marginTop: '20px' }}></div>
                </div>
            )}
            {paymentStatus && <p>Status: {paymentStatus}</p>}
        </div>
    );
};

export default App;
